// ==UserScript==
// @name         PrusaLink Dashboard Notifier
// @namespace    http://tampermonkey.net/
// @version      2024-02-25
// @description  Checks the title for "Attention" and produces a visual and audable notification.
// @author       Splayn and ChatGPT
// @updateURL    https://github.com/Splayn3D/PrusaLinkNotifer/raw/main/PrusaLinkDashboardNotifier.user.js
// @downloadURL  https://github.com/Splayn3D/PrusaLinkNotifer/raw/main/PrusaLinkDashboardNotifier.user.js
// @match        http://mk4nr1/
// @match        http://prusaprinter/
// @grant        GM_notification
// @grant        window.focus
// ==/UserScript==

(function() {
    'use strict';
    let audioNotif = new Audio("data:audio/mpeg;base64,");
    audioNotif.loop = true;

    // =================== OPTIONS =====================================
    audioNotif.volume = 0.7;
    let notifyWhenFinished = true; //Needs testing
    let DebugOn = false;
    // =================================================================

    let statusLastNotifTitle = document.title;
    let statusAttentionTitle = "Attention";
    let statusFinishedTitle = "Finished";
    let statusPrintingTitle = "Printing";
    let statusBusyTitle = "Busy";
    let statusReadyTitle = "Dashboard - Prusa";

    let doNotification = false;

    // Function to check if the title has changed and starts with "Attention"
    function checkTitle() {
        if (DebugOn) console.log("Checking Title");
        if (DebugOn) console.log("Title: '" + document.title +"', statusLastNotifTitle: '"+ statusLastNotifTitle + "'");
        if (DebugOn) console.log("Att: "+(document.title !== statusLastNotifTitle && document.title.startsWith(statusAttentionTitle))+", Fin: "+(document.title !== statusLastNotifTitle && document.title.startsWith(statusFinishedTitle)));
        if (document.title !== statusLastNotifTitle && document.title.startsWith(statusAttentionTitle)) {
            if (DebugOn) console.log("Found Attention in Title");
            doNotification = true;
        }else if (document.title !== statusLastNotifTitle && document.title.startsWith(statusFinishedTitle)) {
            if (DebugOn) console.log("Found Finished in Title");
            if (notifyWhenFinished) doNotification = true;
        }else if(document.title.startsWith(statusReadyTitle) || document.title.startsWith(statusPrintingTitle) || document.title.startsWith(statusBusyTitle)) {
            if (DebugOn) console.log("Title Changed. Notification should trigger again in the future");
            statusLastNotifTitle = "reset for new notif"
        }
        if(doNotification) {
            if (DebugOn) console.log("Doing Notification");
            audioNotif.play();
            showNotification();
            showAlert();
            audioNotif.pause();
            statusLastNotifTitle = document.title; // Set to the same as the one IF block that triggered it so that the notification doesnt repeat after dismissing it.
            doNotification = false;
        }
    }

    // Function to show an alert message
    function showAlert() {
        if (DebugOn) console.log("Doing Alert Msg Box");
        alert("Merp! The printer wants attention!");
    }

    // Function to show a desktop notification
    function showNotification() {
        GM_notification({
            text: "The printer needs attention.",
            title: "Merp",
            //url: 'https:/example.com/',
            onclick: (event) => {
                // The userscript is still running, so don't open example.com
                event.preventDefault();
                // Display an alert message instead
                window.focus ();
                //alert('I was clicked!')
            }
        });
    }

    // Set an interval to check the title periodically (adjust the interval as needed)
    setInterval(checkTitle, 5000); // Check every 1000 milliseconds (1 second)
})();